	public int repeatedNumber(final List<Integer> a) {
		int[] arr = new int[2];
		int[] count = new int[2];

		for (Integer number : a) {
			boolean flag = false;
			for (int i = 0; i < arr.length; i++) {
				if (arr[i] == number) {
					count[i]++;
					flag = true;
					break;
				}
			}
			if (!flag) {
				for (int i = 0; i < count.length; i++) {
					if (count[i] == 0) {
						arr[i] = number;
						count[i]++;
						flag = true;
						break;
					}
				}
			}
			if (!flag) {
				for (int i = 0; i < count.length; i++) {
					count[i]--;
				}
			}
		}

		int times = 0;
		for (int value : arr) {
			for (Integer number : a) {
				if (value == number) {
					times++;
				}
			}
			if (times > (a.size() / 3)) {
				return value;
			}
			times = 0;
		}

		return -1;
	}
